// ============ parâmetros “fáceis de mexer” =============
const inicioExpediente = 8;   // 08:00
const fimExpediente    = 19;  // 19:00  (o último slot será 18:30‑19:00)
const passoMinutos     = 30;  // tamanho do slot
// =======================================================

// 1. Entrada -------------------------------------------------------------
const entrada = $json.concatenated_evento ?? '';

/* Entrada esperada (exemplo):
   data_comeco: 2025-04-17T15:00:00-03:00, data_fim: 2025-04-17T16:00:00-03:00.,
   data_comeco: 2025-04-17T17:00:00-03:00, data_fim: 2025-04-17T17:30:00-03:00.
*/

// 2. Extrai períodos ocupados -------------------------------------------
const busyRegex = /data_comeco:\s*([\d\-T:+]+),\s*data_fim:\s*([\d\-T:+]+)\.?/g;
const ocupados = []; // {iniMin, fimMin}
let m;
while ((m = busyRegex.exec(entrada)) !== null) {
  ocupados.push({
    iniMin: isoToMinutes(m[1]),
    fimMin: isoToMinutes(m[2]),
  });
}

// 3. Gera slots disponíveis ---------------------------------------------
const disponiveis = [];
for (let iniMin = inicioExpediente * 60;
     iniMin < fimExpediente * 60;
     iniMin += passoMinutos) {

  const fimMin = iniMin + passoMinutos;

  // colisão?  (slotStart < busyEnd) && (slotEnd > busyStart)
  const colide = ocupados.some(({iniMin: bIni, fimMin: bFim}) =>
                                iniMin < bFim && fimMin > bIni);
  if (colide) continue;

  disponiveis.push(`${formatHHMM(iniMin)}-${formatHHMM(fimMin)}`);
}

// 4. Retorno -------------------------------------------------------------
return [
  {
    json: {
      horarios_disponiveis: disponiveis.join(', ')
    }
  }
];

// ============ utilitários ==============================================
function isoToMinutes(iso) {
  // “2025‑04‑17T15:00:00‑03:00” → 15*60 + 0
  const timePart = iso.split('T')[1];      // 15:00:00‑03:00
  const [hh, mm] = timePart.split(':');
  return Number(hh) * 60 + Number(mm);
}

function formatHHMM(minutos) {
  const hh = String(Math.floor(minutos / 60)).padStart(2, '0');
  const mm = String(minutos % 60).padStart(2, '0');
  return `${hh}:${mm}`;
}

