const diaDesejado = $json.date || "2025-04-04";

// Regex para extrair os dados de cada agendamento
const regex = /id:\s*([^,]+),\s*data_comeco:\s*([^,]+),\s*data_fim:\s*([^.]+)\.\s*Titulo:\s*([^.,]+)/g;

// Conjunto para armazenar os horários ocupados
const horariosOcupados = new Set();
let match;

while ((match = regex.exec(inputData)) !== null) {
  const dataComeco = match[2].trim();
  // Considera somente os agendamentos que iniciam na data desejada
  if (dataComeco.startsWith(diaDesejado)) {
    // Extrai a parte do horário de início
    const timePartInicio = dataComeco.split("T")[1];
    const horaInicio = parseInt(timePartInicio.substring(0, 2));    
    const minutosInicio = parseInt(timePartInicio.substring(3, 5));   
    // Se os minutos forem >= 30, bloqueia o próximo horário; senão, bloqueia o horário atual
    const slotInicio = minutosInicio >= 30 ? horaInicio + 1 : horaInicio;

    // Extrai a parte do horário de término
    const dataFim = match[3].trim();
    const timePartFim = dataFim.split("T")[1];
    const horaFim = parseInt(timePartFim.substring(0, 2));    
    const minutosFim = parseInt(timePartFim.substring(3, 5));   
    const slotFim = minutosFim >= 30 ? horaFim + 1 : horaFim;

    // Bloqueia todos os horários entre o início e o término (não incluindo o horário de término)
    for (let hora = slotInicio; hora < slotFim; hora++) {
      horariosOcupados.add(hora);
    }
  }
}

// Gera os intervalos disponíveis entre 07:00 e 19:00, removendo os horários ocupados
const horarioDisponivel = [];
for (let hora = 7; hora < 19; hora++) {
  if (!horariosOcupados.has(hora)) {
    // Formata para ter dois dígitos, se necessário
