  const dataComeco = match[2].trim();
  // Considera somente os agendamentos que iniciam na data desejada
  if (dataComeco.startsWith(diaDesejado)) {
    // Processa a parte de início do evento
    const timePartInicio = dataComeco.split("T")[1];
    const horaInicio = parseInt(timePartInicio.substring(0, 2));    
    const minutosInicio = parseInt(timePartInicio.substring(3, 5));   
    // Converte para slot de 30 minutos (cada hora possui 2 slots)
    const slotInicio = horaInicio * 2 + (minutosInicio >= 30 ? 1 : 0);

    // Processa a parte de término do evento
    const dataFim = match[3].trim();
    const timePartFim = dataFim.split("T")[1];
    const horaFim = parseInt(timePartFim.substring(0, 2));    
    const minutosFim = parseInt(timePartFim.substring(3, 5));   
    const slotFim = horaFim * 2 + (minutosFim >= 30 ? 1 : 0);

    // Bloqueia todos os slots de 30 minutos entre o início e o término (slotFim não incluso)
    for (let slot = slotInicio; slot < slotFim; slot++) {
      horariosOcupados.add(slot);
    }
  }
}

// Gera os intervalos disponíveis entre 07:00 e 19:00, em blocos de 30 minutos
const horarioDisponivel = [];
// Cada slot representa 30 minutos; de 07:00 (7*2 = 14) até 19:00 (19*2 = 38, não incluso)
for (let slot = 7 * 2; slot < 19 * 2; slot++) {
  if (!horariosOcupados.has(slot)) {
    const startHour = Math.floor(slot / 2);
    const startMin = (slot % 2 === 0) ? "00" : "30";
    const endSlot = slot + 1;
    const endHour = Math.floor(endSlot / 2);
    const endMin = (endSlot % 2 === 0) ? "00" : "30";
    // Formata para ter dois dígitos, se necessário
    const startStr = (startHour < 10 ? "0" + startHour : startHour) + ":" + startMin;
    const endStr = (endHour < 10 ? "0" + endHour : endHour) + ":" + endMin;
    horarioDisponivel.push(`${startStr}-${endStr}`);
  }
}

// Cria a string final conforme desejado
const resultText = "horarioDisponivel: " + horarioDisponivel.join(", ");

// Retorna como array de objetos (JSON válido)
return [{ horarioDisponivel: resultText }];

