# Agendar reunião v03
// Obtém a string dos eventos concatenados do input
const inputData = $json.concatenated_evento;
// Define o dia desejado (pode vir do input, se não utiliza o padrão "2025-04-15")
const diaDesejado = $json.date || "2025-04-15";

// Regex para extrair os dados de cada agendamento
const regex = /id:\s*([^,]+),\s*data_comeco:\s*([^,]+),\s*data_fim:\s*([^.]+)\.\s*Titulo:\s*([^.,]+)/g;
const eventos = [];
let match;

// Para cada evento encontrado, extrai diretamente os valores de hora e minuto a partir da string
while ((match = regex.exec(inputData)) !== null) {
  const dataComecoStr = match[2].trim();
  const dataFimStr = match[3].trim();

  // Considera somente os agendamentos do dia desejado, conforme a string de data
  if (dataComecoStr.startsWith(diaDesejado)) {
    // Extrai a parte do horário (HH:MM) da string
    // Exemplo: "2025-04-15T16:00:00-03:00" -> pega "16:00"
    const timePartInicio = dataComecoStr.split("T")[1].substring(0, 5);
    const timePartFim = dataFimStr.split("T")[1].substring(0, 5);

    // Separa hora e minutos e converte para número
    const [horaInicio, minutoInicio] = timePartInicio.split(":").map(Number);
    const [horaFim, minutoFim] = timePartFim.split(":").map(Number);

    // Converte para total de minutos desde a meia-noite
    const totalInicio = horaInicio * 60 + minutoInicio;
    const totalFim = horaFim * 60 + minutoFim;

    eventos.push({ start: totalInicio, end: totalFim });
  }
}

// Gera os intervalos disponíveis entre 07:00 e 19:00 (cada um com duração de 1 hora)
// Para cada slot, verifica se há sobreposição com algum evento
const horarioDisponivel = [];
for (let hora = 7; hora < 19; hora++) {
  const slotInicio = hora * 60;
  const slotFim = (hora + 1) * 60;

  // Verifica se existe algum evento cujo horário se sobrepõe com o slot
  const ocupado = eventos.some(ev => ev.start < slotFim && ev.end > slotInicio);

  if (!ocupado) {
    // Formata o horário com dois dígitos, se necessário
    const startStr = hora < 10 ? "0" + hora : "" + hora;
    const endHour = hora + 1;
    const endStr = endHour < 10 ? "0" + endHour : "" + endHour;

    horarioDisponivel.push(`${startStr}:00-${endStr}:00`);
  }
}

// Cria a string final com os horários disponíveis
const resultText = "horarioDisponivel: " + horarioDisponivel.join(", ");
return [{ horarioDisponivel: resultText }];

