// message_chunker.js
// Função para uso em Node (ex.: n8n Function Node) que recebe input via $json

/**
 * Divide uma grande mensagem em chunks preservando listas intactas e calculando tempo por chunk.
 * Não separa textos no formato de disponibilidade de horários com emoji de relógio.
 * @param {string} text - O texto recebido como input.
 * @returns {{chunks: {paragraph: string, sequence_number: number, time: number}[]}}
 */
function splitMessageIntoChunks(text) {
  // Normaliza e trim
  text = text.replace(/\r\n/g, "\n").trim();

  // Padrão de mensagem de disponibilidade de horários (ex.: ⏰ 10:00 ... pergunta)
  const scheduleRegex = /^.+:\n\n(?:⏰.*\n)+\n?.*\?$/m;
  if (scheduleRegex.test(text)) {
    return {
      chunks: [{
        paragraph: text,
        sequence_number: 1,
        time: calculateTempo(text)
      }]
    };
  }

  const chunks = [];
  let seq = 1;
  const blocks = text.split(/\n{2,}/);

  blocks.forEach(block => {
    const lines = block.split("\n");
    // Detecta listas com -, *, números ou emoji de relógio
    const isList = lines.every(line =>
      /^\s*([-*]|\d+\.)\s+/.test(line) || /^\s*⏰/.test(line)
    );

    if (isList) {
      const para = block.trim();
      chunks.push({ paragraph: para, sequence_number: seq++, time: calculateTempo(para) });
    } else {
      const sentences = block.match(/[^.!?]+[.!?]+(?:\s|$)/g) || [block];
      let i = 0;
      while (i < sentences.length) {
        const groupSize = Math.min(sentences.length - i, 2 + Math.floor(Math.random() * 2));
